import React from "react";
import { Routes, Route, useLocation } from "react-router-dom";

import CollectionsOverview from '../../components/collection-overview/collection-overview.component'
import CategoryPage from '../collection/collection.component'

const ShopPage = ({ useLocation }) => (
    <div className='shop-page'>
        <Routes>
            <Route exact path={`${useLocation.path}`} element={<CollectionsOverview/>} />
            <Route path={`${useLocation.path}/:categoryId`} element={<CategoryPage/>} />
        </Routes>
    </div>
)

export default ShopPage;
